mapper.select:
SELECT * FROM 
	(SELECT ROWNUM AS rn, tbl.* FROM 	
		(SELECT * FROM freereply
		WHERE bno = #{bno}
		ORDER BY rno DESC
		) tbl
	)

WHERE rn > (#{paging.pageNum}-1) * #{paging.cpp}
AND rn <= #{paging.pageNum} * #{paging.cpp}

mapper.update:
UPDATE freereply SET 
reply = #{reply}, update_date = sysdate
WHERE rno = #{rno}
